import React, { useRef } from "react";
import classes from "./Checkout.module.css";
import useInputValidity from "../UI/use-inputValidity";

const Checkout = (props) => {
    // const nameRef= useRef();
    // const streetRef= useRef();
    // const postalRef= useRef();
    // const cityRef= useRef();

    const isEmpty = value => value.trim().length === 0;
    const is5Chars = value => value.trim().length ===5;

    const {
        enteredInput: enteredName,
        isInputValid : isNameValid,
        hasError: nameHasError,
        inputChangehandler: nameChangeHandler,
        inputBlurhandler : nameBlurHandler,
        resetInput: resetName
    } = useInputValidity(isEmpty);



  const confirmHandler = (ev) => {
    ev.preventDefault();




    const enteredName = nameRef.current.value;
    const enteredStreet = streetRef.current.value;
    const enteredPostal = postalRef.current.value;
    const enteredCity = cityRef.current.value;

    const nameValid= !isEmpty(enteredName);
    const streetValid= !isEmpty(enteredStreet);
    const postalValid= !isEmpty(enteredPostal);
    const cityValid= !is5Chars(enteredCity);

    const formValid = nameValid && streetValid && postalValid && cityValid;

    if(!formValid){
        return;
    }

    console.log(enteredName, enteredStreet, enteredPostal, enteredCity);
    resetName();

  };

  return (
    <form className={classes.form} onSubmit={confirmHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Your Name</label>
        <input type="text" id="name" value={enteredName} onChange={nameChangeHandler} onBlur={nameBlurHandler}/>
      </div>
      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input type="text" id="street" ref={streetRef}/>
      </div>
      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input type="text" id="postal" ref={postalRef}/>
      </div>
      <div className={classes.control}>
        <label htmlFor="city">City</label>
        <input type="text" id="city" ref={cityRef}/>
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
